import React, { useState } from 'react';
import {
  Container,
  Navbar,
  Nav,
  Row,
  Col,
  Image,
  Form,
  FormControl,
  Button,
} from 'react-bootstrap';
import {
  Facebook,
  Twitter,
  Instagram,
  Linkedin,
  Search,
} from 'react-bootstrap-icons';

const Header = () => {
  const [showMenu, setShowMenu] = useState(false);

  const handleToggle = () => {
    setShowMenu(!showMenu);
  };

  const closeMenu = () => {
    setShowMenu(false);
  };

  return (
    <Container fluid className="px-4">
      {/* Logo Section (centered on desktop) */}
      <Row className="py-4 border-bottom align-items-center d-none d-lg-flex">
        <Col className="text-center">
          <div className="d-flex align-items-center justify-content-center gap-2 py-4">
            <Image
              src="https://cdn2.iconfinder.com/data/icons/social-flat-buttons-3/512/blog-512.png"
              alt="Logo"
              roundedCircle
              width={50}
              height={50}
            />
            <h1 className="fw-bold mb-0 fs-4">Anamachoru</h1>
          </div>
        </Col>
      </Row>

      {/* Navigation Bar */}
      <Navbar expand="lg" className="p-0">
        <Container fluid className="p-0">
          {/* Mobile logo & toggle */}
          <div className="d-flex w-100 justify-content-between align-items-center d-lg-none px-3 py-2 border-bottom">
            <div className="d-flex align-items-center gap-2">
              <Image
                src="https://cdn2.iconfinder.com/data/icons/social-flat-buttons-3/512/blog-512.png"
                alt="Logo"
                roundedCircle
                width={30}
                height={30}
              />
              <span className="fw-bold fs-6 py-3">Anamachoru</span>
            </div>
            <Navbar.Toggle
              aria-controls="main-nav"
              onClick={handleToggle}
            />
          </div>

          {/* Desktop Menu */}
          <Navbar.Collapse id="main-nav" className="justify-content-center d-none d-lg-flex">
            <Nav className="align-items-center py-3">
              {[
                'HOME',
                'HOUSE STORIES',
                 'BLOG',
                'BECOME A PRO',
                'HELP',
                'HOME DECORATION',
                'PROPERTYPRO',
              ].map((item, index) => (
                <Nav.Link
                  key={index}
                  href="#"
                  className="text-uppercase px-3 d-flex align-items-center"
                  style={{
                    height: '100%',
                    fontSize: '0.75rem',
                    transition: 'background-color 0.2s',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#0d6efd';
                    e.currentTarget.style.color = 'white';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'transparent';
                    e.currentTarget.style.color = '';
                  }}
                >
                  {item}
                </Nav.Link>
              ))}
              {[Facebook, Twitter, Instagram, Linkedin].map((Icon, idx) => (
                <Nav.Link
                  key={idx}
                  href="#"
                  className="d-flex align-items-center px-2"
                  style={{
                    height: '100%',
                    transition: 'background-color 0.2s',
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#0d6efd';
                    e.currentTarget.style.color = 'white';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'transparent';
                    e.currentTarget.style.color = '';
                  }}
                >
                  <Icon size={12} />
                </Nav.Link>
              ))}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Mobile Sidebar Menu */}
      <div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          height: '100vh',
          width: '75%',
          maxWidth: '300px',
          backgroundColor: '#111',
          color: 'white',
          transform: showMenu ? 'translateX(0)' : 'translateX(-100%)',
          transition: 'transform 0.3s ease',
          zIndex: 1050,
          display: 'flex',
          flexDirection: 'column',
          padding: '2rem 1rem',
        }}
      >
        {/* Close button */}
        <Button
          variant="light"
          size="sm"
          className="mb-4 align-self-end"
          onClick={closeMenu}
        >
          âœ•
        </Button>

        {/* Menu items */}
        <Nav className="flex-column align-items-start w-100">
          {[
                'HOME',
                'HOUSE STORIES',
                 'BLOG',
                'BECOME A PRO',
                'HELP',
                'HOME DECORATION',
                'PROPERTYPRO',
          ].map((item, index) => (
            <Nav.Link
              key={index}
              href="#"
              onClick={closeMenu}
              className="text-uppercase small py-2 text-white w-100"
              style={{
                fontSize: '0.8rem',
              }}
            >
              {item}
            </Nav.Link>
          ))}
        </Nav>

        {/* Spacer pushes search + icons to bottom */}
        <div className="mt-auto w-100">
          <Form className="d-flex mb-3">
            <FormControl
              type="search"
              placeholder="Search..."
              className="me-2"
              aria-label="Search"
            />
            <Button variant="primary">
              <Search size={14} />
            </Button>
          </Form>

          <div className="d-flex gap-3">
            <a href="#"><Facebook size={16} color="white" /></a>
            <a href="#"><Twitter size={16} color="white" /></a>
            <a href="#"><Instagram size={16} color="white" /></a>
            <a href="#"><Linkedin size={16} color="white" /></a>
          </div>
        </div>
      </div>

      {/* Overlay for closing menu */}
      {showMenu && (
        <div
          onClick={closeMenu}
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            height: '100vh',
            width: '100vw',
            backgroundColor: 'rgba(0,0,0,0.5)',
            zIndex: 1040,
          }}
        />
      )}
    </Container>
  );
};

export default Header;
