import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

// Assuming you have an image for the happiness guarantee

const HappinessGuarantee = () => {
  return (
    <section className="py-5 bg-light">
      <Container>
        <Row className="align-items-center">
          {/* Image Column - hidden on mobile, visible on desktop */}
          <Col md={5} className="d-none d-md-block">
            <img 
              src={'https://cache-landingpages.services.handy.com/assets/pages/services/furniture_assembly_img-016e6663e768c07e91185c00ca737ee7292df33f15715f5e63d131155683a29b.png'} 
              alt="Happiness Guarantee" 
              className="img-fluid rounded"
              style={{ maxHeight: '400px', objectFit: 'cover' }}
            />
          </Col>

          {/* Text Content Column */}
          <Col md={7}>
            <h2 className="mb-4">
              Your Happiness,<br />
              Guaranteed
            </h2>
            
            <p className="lead mb-4">
              Your happiness is our goal. If you're not happy, we'll work to make it right. 
              Our friendly customer service agents are available 24 hours a day, 7 days a week. 
              The Handy Happiness Guarantee only applies when you book and pay for a service 
              directly through the Handy platform.
            </p>
            
            <Button variant="outline-primary" className="mt-3">
              Learn more
            </Button>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default HappinessGuarantee;
